import scrapy
from scrapy import FormRequest


class CpucSpider(scrapy.Spider):
    name = "cpuc"
    allowed_domains = ['docs.cpuc.ca.gov']

    start_urls = ['http://docs.cpuc.ca.gov/advancedsearchform.aspx']

    def parse(self, response):
        formdata = {
                    '__EVENTARGUMENT': '',
                    '__EVENTTARGET': '',
                    'FilingDateFrom': '06/16/19',
                    'FilingDateTo': '06/17/19',
                    '__VIEWSTATEGENERATOR': '6DB12421',
                    'DocTitle': '',
                    'ddlCpuc01Types': '-1',
                    'IndustryID': '-1',
                    'ProcNum': '',
                    'MeetDate': '',
                    'PubDateFrom': '',
                    'PubDateTo': '',
                    'EfileConfirmNum': '',
                    'SearchButton': 'Search',
                    '__VIEWSTATE': '',
                    '__EVENTVALIDATION': ''
                    }
        yield FormRequest.from_response(response,
                                        formdata=formdata,  method='POST', callback=self.parse_search_result)

    def parse_search_result(self, response):
        table_rows = response.xpath("//table[@id='ResultTable']/tbody/tr")
        skip = False
        for row in table_rows:
            if not skip:
                title = row.xpath("td[@class='ResultTitleTD']/text()").get()
                print(title)
                skip = True
            else:
                skip = False

